import React from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import Spinner from 'react-bootstrap/Spinner'
import oauth from 'axios-oauth-client';

import {
    Button,
    Container,
    Row,
    Col,
    Card,
    CardBody,
    CardImg,
    CardTitle,
    CardText
} from "reactstrap";
//import { collapseTextChangeRangesAcrossMultipleVersions } from 'typescript';

class Biddings extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            posts: [],
            catalogue: [],
            currentIndex: 0,
            itemShow: 3,
            random: 0,
            loading: true,
            error: null,
            token: ""
        };
    }

    fetchBiddings = () => {

        const instance = axios.create({
            baseURL: 'https://some-domain.com/api/',
            timeout: 1000,
            headers: { 'Authorization': 'Bearer ' + token }
        });

        axios.post(
            'http://localhost:8000/api/v1/get_token_payloads',
            bodyParameters,
            config
        ).then(console.log).catch(console.log);

        axios.post('http://desmond.business:8080/fyp/authenticate', {
            "username": "des123",
            "password": "password"
        })
            .then(response => {
                console.log("authenticate success");
                const token = response.data.token;
                console.log(token);
                const config = {
                    headers: { Authorization: `Bearer ${token}` }
                };
                var post_data = {
                    'Access-Control-Allow-Origin': '*',
                    'Authorization': `Bearer ${token}`,
                    'Cache-Control': "no-cache",
                }
                console.log(post_data)
                axios.get('http://desmond.business:8080/fyp/getBidding', {headers: post_data}).then(res => { console.log(res) });
            })
            .catch(function (error) {
                console.log("authenticate FAIL")
                console.log(error);
            });

        axios.get("http://desmond.business:8080/fyp/getBiddings")
            .then(res => {
                // Transform the raw data by extracting the nested posts
                const updateCatalogue = this.state.catalogue
                const posts = res.data.results;
                for (var i = 0; i < this.state.itemShow; i++) {
                    console.log(this.state.itemSHow)
                    console.log("i=" + i)
                    const min = 0;
                    const max = posts.length;
                    const rand = parseInt(min + Math.random() * (max - min))

                    console.log("rand" + rand)
                    updateCatalogue.push(
                        <Col key={posts[rand].id} className="col-md-4">
                            <Card style={{ width: '20rem' }}>
                                <CardImg top src={require("assets/img/mac_apple.jpg")} alt="..." />
                                <CardBody>
                                    <CardTitle>{posts[rand].title}</CardTitle>
                                    <CardText>{posts[rand].create_timestamp}</CardText>
                                    <Link to={`/CarsforSale/${posts[rand].id}`}>
                                        <Button color="primary">
                                            ${posts[rand].selling_price}
                                        </Button>
                                    </Link>
                                </CardBody>
                            </Card>
                        </Col>
                    )
                }
                console.log(this.state.catalogue);
                //console.log(posts);
                // Update state to trigger a re-render.
                // Clear any errors, and turn off the loading indiciator.
                this.setState({
                    posts,
                    catalogue: updateCatalogue,
                    loading: false,
                    error: null
                });
            })
            .catch(err => {
                // Something went wrong. Save the error in state and re-render.
                this.setState({
                    loading: false,
                    error: err
                });
            });
    }

    componentDidMount() {
        // Remove the 'www.' to cause a CORS error (and see the error state)
        this.fetchBiddings()
    }

    showMore = () => {
        this.fetchBiddings()
        console.log(this.state.catalogue);
        console.log(this.state.currentIndex);
    }


    renderLoading() {
        return <div>Loading...</div>;
    }

    renderError() {
        return (
            <div>
                Uh oh: {this.state.error.message}
            </div>
        );
    }

    renderPosts() {
        if (this.state.error) {
            return this.renderError();
        }

        console.log(this.state.posts[0]);
        return (
            <h6>
                Success getting Bidding Records
            </h6>

        );
    }

    render() {
        const catalogue = this.state.catalogue
        if (this.state.loading) {
            return (
                <div className="section">
                    <Container className="text-center">
                        <Card className="text-center">
                            <Row>
                                <Col>
                                    <div className="title">
                                        <h1 className="bd-title">{this.props.title}</h1>
                                    </div>
                                </Col>
                            </Row>
                            <div>
                                <Spinner animation="border" variant="primary" />
                            </div>

                        </Card>
                    </Container>
                </div>
            )
        } else {
            return (
                <div className="section">
                    <Container className="text-center">
                        <Card className="text-center">
                            <Row>
                                <Col>
                                    <div className="title">
                                        <h1 className="bd-title">{this.props.title}</h1>
                                    </div>
                                </Col>
                            </Row>
                            <CardBody>
                                <Row>
                                    {catalogue}
                                </Row>
                            </CardBody>
                        </Card>
                    </Container>
                </div>
            );
        }
    }
}

export default Biddings;
