import React from 'react'
import axios from 'axios';

class Fashion extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            loading: false,
            error: null,
            carsProperty: [],
            category:"Fashion"
        }
    }

    componentDidMount(){
        this.renderCarsProperty()
    }

    renderCarsProperty = () => {

        return(
        axios.get("http://desmond.business:8080/fyp/getCategoryFirstLvs")
            .then(res => {
                // Transform the raw data by extracting the nested posts

                const posts = res.data.results;
                //console.log(posts);
                // Update state to trigger a re-render.
                // Clear any errors, and turn off the loading indiciator.
                posts.forEach((elm) => {
                    if(elm.category_first_lv_name == this.state.category){
                        console.log(elm)
                    this.state.carsProperty.push(elm.theCategorySecondLvs.map((obj, index) =>
                        <tr key={obj.id}>
                            <td id={obj.id}><a href="/CarsForSale">{obj.category_second_lv_name}</a></td>
                        </tr>
                        ))
                    }
                })

                console.log(this.state.carsProperty)
                this.setState({
                    loading: false,
                    error: null
                });
            })
            .catch(err => {
                // Something went wrong. Save the error in state and re-render.
                this.setState({
                    loading: false,
                    error: err
                });
            })
        )
    }

    render() {
        return (
            <div>
                <table id='FashionTable'>
                    <tbody>
                        <tr>
                            <th>
                            {this.state.category.toUpperCase()}
                            </th>
                            </tr>
                        <tr key="1">
                        {this.state.carsProperty}
                            </tr>
                    </tbody>
                </table>
            </div>
        )
    }
}

export default Fashion;
