/*!

=========================================================
* Paper Kit React - v1.0.0
=========================================================

* Product Page: https://www.creative-tim.com/product/paper-kit-react

* Copyright 2019 Creative Tim (https://www.creative-tim.com)
* Licensed under MIT (https://github.com/creativetimofficial/paper-kit-react/blob/master/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React from "react";
import CategoryTable from "components/Category/CarsProperty.js";
import Fashion from "components/Category/Fashion.js";
import axios from "axios";
// reactstrap components
import {
  Card,
  CardBody,
  NavItem,
  NavLink,
  Nav,
  TabContent,
  TabPane,
  Container,
  Row,
  Col
} from "reactstrap";

// core components

function SectionProgress() {
  const [activeTab, setActiveTab] = React.useState("1");
  const toggle = tab => {
    if (activeTab !== tab) {
      setActiveTab(tab);
    }
  };

  axios.get("http://desmond.business:8080/fyp/getCategoryFirstLvs")
  .then(res => {
      // Transform the raw data by extracting the nested posts

      const posts = res.data.results;
      //console.log(posts);
      // Update state to trigger a re-render.
      // Clear any errors, and turn off the loading indiciator.
        posts.forEach((elm) => {
            if(elm.category_first_lv_name == this.state.category){
                const carsPropertyItem = elm.theCategorySecondLvs
            }
        })
        console.log(carsPropertyItem)
      console.log(this.state.carsProperty)
      this.setState({
          loading: false,
          error: null
      });
  })
  .catch(err => {
      // Something went wrong. Save the error in state and re-render.
      this.setState({
          loading: false,
          error: err
      });
  })

  const data = [
    {'fruit': 'Apple', 'cost': 100},
    {'fruit': 'Orange', 'cost': 50},
    {'fruit': 'Banana', 'cost': 35},
    {'fruit': 'Mango', 'cost': 70},
    {'fruit': 'Pineapple', 'cost': 45},
    {'fruit': 'Papaya', 'cost': 40},
    {'fruit': 'Watermelon', 'cost': 35}
    ]
  return (
    <>
      <div className="section">
        <Container>
        <Card className="text-center">
                        <CardBody>
          <br />
          <div className="nav-tabs-navigation">
            <div className="nav-tabs-wrapper">
              <Nav id="tabs" role="tablist" tabs>

                <NavItem>
                      <NavLink
                        className={activeTab === "1" ? "active" : ""}
                        onClick={() => {
                          toggle("1");
                        }}
                      >
                        Cars & Property
                      </NavLink>
                </NavItem>
                <NavItem>
                  <NavLink
                    className={activeTab === "2" ? "active" : ""}
                    onClick={() => {
                      toggle("2");
                    }}
                  >
                    Fashion
                      </NavLink>
                </NavItem>
                <NavItem>
                  <NavLink
                    className={activeTab === "3" ? "active" : ""}
                    onClick={() => {
                      toggle("3");
                    }}
                  >
                    Home & Living
                      </NavLink>
                </NavItem>
                <NavItem>
                  <NavLink
                    className={activeTab === "4" ? "active" : ""}
                    onClick={() => {
                      toggle("4");
                    }}
                  >
                    Mobiles & Electronics
                      </NavLink>
                </NavItem>
                <NavItem>
                  <NavLink
                    className={activeTab === "5" ? "active" : ""}
                    onClick={() => {
                      toggle("5");
                    }}
                  >
                    Hobbies & Games
                      </NavLink>
                </NavItem>
                <NavItem>
                  <NavLink
                    className={activeTab === "6" ? "active" : ""}
                    onClick={() => {
                      toggle("6");
                    }}
                  >
                    Other
                      </NavLink>
                </NavItem>
              </Nav>
            </div>
          </div>
          <TabContent activeTab={activeTab} className="text-center">
            <TabPane tabId="1">
              <CategoryTable data={data}/>
            </TabPane>
            <TabPane tabId="2">
              <Fashion></Fashion>
            </TabPane>
            <TabPane tabId="3">
              <p>Here are your messages.</p>
            </TabPane>
          </TabContent>
          </CardBody>
          </Card>
        </Container>
      </div>{" "}
    </>
  );
}

export default SectionProgress;
