import React from 'react'
import axios from 'axios';
import CategoryTable from './CategoryTable.js'
import { css } from "@emotion/core";
import ClipLoader from "react-spinners/ClipLoader";

class CarsProperty extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            loading: null,
            error: null,
            data : null,
            carsPropertyHeader:[],
            carsPropertyItem: [],
            category: "Cars & Property",
            fetch : "http://desmond.business:8080/fyp/getCategoryFirstLvs"
        }
    }

     componentDidMount(){
         this.setState({
             loading:true,
             error:false
         })
        axios.get(this.state.fetch)
        .then(res => {
            // Transform the raw data by extracting the nested posts

            const posts = res.data.results;
            const list = null;
            //console.log(posts);
            // Update state to trigger a re-render.
            // Clear any errors, and turn off the loading indiciator.
            posts.forEach((elm) => {
                if(elm.category_first_lv_name == this.state.category){
                    console.log(elm)
                    list = elm.theCategorySecondLvs
                }
            })
            this.setState({
                data: list,
                loading: false,
                error: null
            });
        })
        .catch(err => {
            // Something went wrong. Save the error in state and re-render.
            this.setState({
                loading: false,
                error: err
            });
        })
    }

    render() {
        const { data, loading } = this.state;
        const override = css`
  display: block;
  margin: 0 auto;
  border-color: red;
`;
        return (
            {loading} ? <LoadingSpinner />: <CategoryTable data={this.state.data}/>
        )
    }
}

export default CarsProperty;
