import React from 'react'
import axios from 'axios';
import CategoryTable from './CategoryTable.js'
import Spinner from 'react-bootstrap/Spinner'

class CarsProperty extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            loading: true,
            error: null,
            data: [],
            category: "Cars & Property",
            fetch: "http://desmond.business:8080/fyp/getCategoryFirstLvs"
        }
    }

    fetchData = () => {
        const list = null
        this.setState({
            data: null,
            loading: true,
            error: null
        })
        axios.get(this.state.fetch)
            .then(res => {
                // Transform the raw data by extracting the nested posts

                const posts = res.data.results;
                //console.log(posts);
                // Update state to trigger a re-render.
                // Clear any errors, and turn off the loading indiciator.
                this.setState({
                    data : () => {}
                })
                posts.forEach((elm) => {
                    if (elm.category_first_lv_name == this.state.category) {   
                        console.log("theCategorySecondLvs")
                        console.log(elm.theCategorySecondLvs)
                        return elm.theCategorySecondLvs
                    }
                })
                console.log(list)
                this.setState({
                    data: list,
                    loading: false
                })
            })
            .catch(err => {
                // Something went wrong. Save the error in state and re-render.
                this.setState({
                    loading: true,
                    error: err
                });
            })
            console.log(this.state.data)
    }

    componentDidMount() {
        this.fetchData()
    }

    isLoading = () =>{
        return this.state.loading
    }
    render() {
        const { data, loading } = this.state;
        return (
            <div>

                {this.isLoading() ? (
                    <Spinner animation="border" variant="primary" />
                ) : (
                        <h6>Landed Data</h6>
                    )}
            </div>
        )
    }
}

export default CarsProperty;
