import React from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';

import {
    Button,
    FormGroup,
    Input,
    Container,
    Row,
    Col
} from "reactstrap";

class Form extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            posts: [],
            loading: true,
            error: null
        };
    }

    postForm() {
        xios.post('/user', {
            firstName: 'Fred',
            lastName: 'Flintstone'
        })
            .then(function (response) {
                console.log(response);
            })
            .catch(function (error) {
                console.log(error);
            });
    }

    componentDidMount() {
        // Remove the 'www.' to cause a CORS error (and see the error state)
        axios.get("http://desmond.business:8080/fyp/getBiddings")
            .then(res => {
                // Transform the raw data by extracting the nested posts

                const posts = res.data.results.map(obj => obj.id);
                //console.log(posts);
                // Update state to trigger a re-render.
                // Clear any errors, and turn off the loading indiciator.
                this.setState({
                    posts,
                    loading: false,
                    error: null
                });
            })
            .catch(err => {
                // Something went wrong. Save the error in state and re-render.
                this.setState({
                    loading: false,
                    error: err
                });
            });
    }

    renderLoading() {
        return <div>Loading...</div>;
    }

    renderError() {
        return (
            <div>
                Uh oh: {this.state.error.message}
            </div>
        );
    }

    renderPosts() {
        if (this.state.error) {
            return this.renderError();
        }

        console.log(this.state.posts[0]);
        return (
            <h6>
                Success getting Bidding Records
            </h6>
        );
    }

    render() {
        const { posts } = this.state;
        return (
            
            <div className="section section-buttons">
                <Container>
                    <div className="title">
                        <h3>Sell Information</h3>
                    </div>
                    <Row>
                        <Col sm="3">LastID</Col>
                        <Col sm="4">
                            <FormGroup>
                                <Input placeholder={posts[0]+1} type="text" />
                            </FormGroup>
                        </Col>
                    </Row>
                    <Row>
                        <Col sm="3">Product Title</Col>
                        <Col sm="4">
                            <FormGroup>
                                <Input placeholder="Product Title" type="text" />
                            </FormGroup>
                        </Col>
                    </Row>
                    <Row>
                        <Col sm="3">Bidding Price</Col>
                        <Col sm="4">
                            <FormGroup>
                                <Input placeholder="Bidding Price" type="text" />
                            </FormGroup>
                        </Col>
                    </Row>
                    <Row>
                        <Col sm="3">seller_customer_id</Col>
                        <Col sm="4">
                            <FormGroup>
                                <Input placeholder="seller_customer_id" type="text" />
                            </FormGroup>
                        </Col>
                        <Col>
                            <Button
                                className="btn-round mr-1"
                                color="primary"
                                type="button"
                                onClick = "post()"
                            >
                                Sell this out !!!
                </Button>
                        </Col>
                    </Row>
                    <br />
                </Container>
            </div>
        );
    }
}

export default Form;
