import React from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import Spinner from 'react-bootstrap/Spinner'
import oauth from 'axios-oauth-client';

import {
    Button,
    Container,
    Row,
    Col,
    Card,
    CardBody,
    CardImg,
    CardTitle,
    CardText
} from "reactstrap";
//import { collapseTextChangeRangesAcrossMultipleVersions } from 'typescript';

class Biddings extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            posts: [],
            catalogue: [],
            currentIndex: 0,
            itemShow: 3,
            random: 0,
            loading: true,
            error: null,
            token: ""
        };
    }

    fetchToken = async () => {
        const res = await axios.post('http://desmond.business:8080/fyp/authenticate', {
            "username": "des123",
            "password": "password"
        })
        return res;
    }
    fetchBiddings = async () => {
        const res = await this.fetchToken();
        const token = res.data.token;
        console.log(token);

        let config = {
            headers: {
                "Authorization": `token ${token}`,
                "Cache-Control": "no-cache",
                "Accept":"*/*"
            }
        }

        console.log(config);
        var response = await axios.post("http://desmond.business:8080/fyp/getBiddings", config)
