import React from 'react';
import axios from 'axios';
import Modal from 'react-modal';

import {
    Button,
    FormGroup,
    Input,
    Container,
    Row,
    Col
} from "reactstrap";

class Form extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            biddings: [],
            category: [],
            lastID: "",
            title: "",
            sellingPrice: "",
            sellerID: "",
            loading: true,
            error: null
        };

    }

    postForm(lastID, title, price, sellerID) {
        axios.post("http://desmond.business:8080/fyp/postBidding", {
            "id": lastID,
            "title": title,
            "category_third_lv_id": null,
            "condition_id": null,
            "details": null,
            "selling_price": null,
            "bidding_price": price,
