import React from 'react'
import axios from 'axios';
import CategoryTable from './CategoryTable.js'
import Spinner from 'react-bootstrap/Spinner'

class CarsProperty extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            loading: true,
            error: null,
            data: [],
            category: "Cars & Property",
            category_second_lv_name: null,
            fetch: "http://desmond.business:8080/fyp/getCategoryFirstLvs"
        }
    }

    async componentDidMount() {
        console.log(this.state.loading)
        this.setState({
            data: null,
            loading: true,
            error: null
        })
        const sample = [
            { 'fruit': 'Apple', 'cost': 100 },
            { 'fruit': 'Orange', 'cost': 50 },
            { 'fruit': 'Banana', 'cost': 35 },
            { 'fruit': 'Mango', 'cost': 70 },
            { 'fruit': 'Pineapple', 'cost': 45 },
            { 'fruit': 'Papaya', 'cost': 40 },
            { 'fruit': 'Watermelon', 'cost': 35 }
        ]
        console.log(sample);
        console.log(this.state.loading)
        await axios.get(this.state.fetch)
            .then(res => {
                // Transform the raw data by extracting the nested posts

                const posts = res.data.results;
                const item = []
                console.log(posts);
                // Update state to trigger a re-render.
                // Clear any errors, and turn off the loading indiciator.
                posts.forEach((elm) => {
                    if (elm.category_first_lv_name == this.state.category) {
                        const mapping = {}
                        for (var i = 0; i < elm.theCategorySecondLvs.length; i++) {
                            for (var k = 0; k < elm.theCategorySecondLvs[i].theCategoryThirdLvLv.length; k++) {
                                mapping[elm.theCategorySecondLvs[i].category_second_lv_name] = elm.theCategorySecondLvs[i].theCategoryThirdLvLv[k].category_third_lv_name

                                console.log("Mapping")
                                console.log(mapping)
                                item.push(mapping)
                            }

                        }
                    }
                    this.setState({
                        data: item
                    })
                })
                this.setState({
                    loading: false
                })
                console.log(this.state.loading)
            })
            .catch(err => {
                // Something went wrong. Save the error in state and re-render.
                this.setState({
                    loading: false,
                    error: err
                });
            })
        console.log(this.state.data)
        console.log(this.state.loading)
    }

    isLoading = () => {
        return this.state.loading
    }

    dataisNull = () => {
        return this.state.data == null
    }

    render() {
        const { data, loading } = this.state;
        const sample = [
            { 'fruit': 'Apple', 'cost': 100 },
            { 'fruit': 'Orange', 'cost': 50 },
            { 'fruit': 'Banana', 'cost': 35 },
            { 'fruit': 'Mango', 'cost': 70 },
            { 'fruit': 'Pineapple', 'cost': 45 },
            { 'fruit': 'Papaya', 'cost': 40 },
            { 'fruit': 'Watermelon', 'cost': 35 }
        ]

        return (
            <div>

                {this.state.loading ? (
                    <Spinner animation="border" variant="primary" />
                ) : (
                        <CategoryTable data={data} />
                    )}
            </div>
        )
    }
}

export default CarsProperty;
