import React from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';
import Modal from 'react-modal';
import {
    openModal,
    modalIsOpen,
    afterOpenModal,
    closeModal,
    customStyles,
    subtitle
} from 'react-modal';

import {
    Button,
    FormGroup,
    Input,
    Container,
    Row,
    Col
} from "reactstrap";

class Form extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            posts: [],
            lastID: "",
            title: "",
            sellingPrice: "",
            sellerID: "",
            loading: true,
            error: null
        };

        const customStyles = {
            content: {
                top: '50%',
                left: '50%',
                right: 'auto',
                bottom: 'auto',
                marginRight: '-50%',
                transform: 'translate(-50%, -50%)'
            }
        };

        Modal.setAppElement('#yourAppElement')
    }

    postForm(lastID, title, price, sellerID) {
        axios.post("http://desmond.business:8080/fyp/postBidding", {
            "id": lastID,
            "title": title,
            "category_third_lv_id": null,
            "condition_id": null,
            "details": null,
            "selling_price": null,
            "bidding_price": price,
            "closing_date_timestmp": null,
            "theBiddingPhotos": {
                "id": 118,
                "photo1": null,
                "photo_type1": null,
                "photo2": null,
                "photo_type2": null,
                "photo3": null,
                "photo_type3": null,
                "create_timestamp": "2020-05-17 15:14:00",
                "modify_timestamp": null
            },
            "bidding_currency": null,
            "district_id": null,
            "seller_customer_id": sellerID,
            "buyer_customer_id": null,
            "bidding_status_id": 1,
            "logistics_di_id": null,
            "payment_id": null,
            "seller_ref_id": null,
            "buyer_ref_id": null,
            "remarks": null,
            "create_timestamp": "2020-05-17 15:14:00",
            "modify_timestamp": null
        })
            .then(function (response) {
                console.log(response);
                this.setState({
                    modalIsOpen: true
                })
            })
            .catch(function (error) {
                console.log(error);
            });
    }
    
    handleSubmit = (event) => {
        event.preventDefault()
        axios.post('http://desmond.business:8080/fyp/postBidding', {
            "id": this.state.lastID,
            "title": this.state.title,
            "category_third_lv_id": null,
            "condition_id": null,
            "details": null,
            "selling_price": this.state.sellingPrice,
            "bidding_price": null,
            "closing_date_timestmp": null,
            "theBiddingPhotos": {
                "id": 118,
                "photo1": null,
                "photo_type1": null,
                "photo2": null,
                "photo_type2": null,
                "photo3": null,
                "photo_type3": null,
                "create_timestamp": "2020-05-17 15:14:00",
                "modify_timestamp": null
            },
            "bidding_currency": null,
            "district_id": null,
            "seller_customer_id": 31,
            "buyer_customer_id": null,
            "bidding_status_id": this.state.sellerID,
            "logistics_di_id": null,
            "payment_id": null,
            "seller_ref_id": null,
            "buyer_ref_id": null,
            "remarks": null,
            "create_timestamp": "2020-05-17 15:14:00",
            "modify_timestamp": null
        })
            .then(function (response) {
                console.log("Successful posting!!!")
                console.log(response);
            })
            .catch(function (error) {
                console.log("Error~~~ posting!!!")
                console.log(error);
            });
    }

    handleInputChange = (event) => {
        event.preventDefault()
        console.log(event)
        console.log(event.target.name)
        console.log(event.target.value)
        this.setState({
            [event.target.name]: event.target.value
        })
    }

    componentDidMount() {
        // Remove the 'www.' to cause a CORS error (and see the error state)
        axios.get("http://desmond.business:8080/fyp/getBiddings")
            .then(res => {
                // Transform the raw data by extracting the nested posts

                const posts = res.data.results;
                //console.log(posts);
                // Update state to trigger a re-render.
                // Clear any errors, and turn off the loading indiciator.
                this.setState({
                    posts,
                    lastID: posts[0].id + 1,
                    title: posts[0].title,
                    sellingPrice: posts[0].selling_price,
                    sellerID: posts[0].seller_customer_id,
                    loading: false,
                    error: null
                });
            })
            .catch(err => {
                // Something went wrong. Save the error in state and re-render.
                this.setState({
                    loading: false,
                    error: err
                });
            });
    }

    renderLoading() {
        return <div>Loading...</div>;
    }

    renderError() {
        return (
            <div>
                Uh oh: {this.state.error.message}
            </div>
        );
    }

    renderPosts() {
        if (this.state.error) {
            return this.renderError();
        }

        console.log(this.state.posts[0]);
        return (
            <h6>
                Success getting Bidding Records
            </h6>
        );
    }

    render() {
        const { posts } = this.state;

        return (
            
            <div className="section section-buttons">
                <Container>
                    <div>
                        <button onClick={openModal}>Open Modal</button>
                        <Modal
                            isOpen={modalIsOpen}
                            onAfterOpen={afterOpenModal}
                            onRequestClose={closeModal}
                            style={customStyles}
                            contentLabel="Example Modal"
                        >

                            <h2 ref={_subtitle => (subtitle = _subtitle)}>Hello</h2>
                            <button onClick={closeModal}>close</button>
                            <div>I am a modal</div>
                            <form>
                                <input />
                                <button>tab navigation</button>
                                <button>stays</button>
                                <button>inside</button>
                                <button>the modal</button>
                            </form>
                        </Modal>
                    </div>
                    <div className="title">
                        <h3>Sell Information</h3>
                    </div>
                    <form onSubmit={this.handleSubmit}>
                    <Row>
                        <Col sm="3">LastID</Col>
                        <Col sm="4">
                            <FormGroup>
                                <Input
                                    id="lastID"
                                    name="lastID"
                                    placeholder={this.state.lastID}
                                    type="text"
                                    onChange={this.handleInputChange}
                                    ref={(input) => this.myinput = input} />
                            </FormGroup>
                        </Col>
                    </Row>
                    <Row>
                        <Col sm="3">Product Title</Col>
                        <Col sm="4">
                            <FormGroup>
                                    <Input
                                        id="title"
                                        name="title"
                                        placeholder={this.state.title}
                                        type="text"
                                        onChange={this.handleInputChange}
                                        ref={(input) => this.myinput = input} />
                            </FormGroup>
                        </Col>
                    </Row>
                    <Row>
                        <Col sm="3">Selling Price</Col>
                        <Col sm="4">
                            <FormGroup>
                                    <Input
                                        id="sellingPrice"
                                        name="sellingPrice"    
                                        placeholder={this.state.sellingPrice}
                                        type="text"
                                        onChange={this.handleInputChange}
                                        ref={(input) => this.myinput = input} />
                            </FormGroup>
                        </Col>
                    </Row>
                    <Row>
                        <Col sm="3">seller_customer_id</Col>
                        <Col sm="4">
                            <FormGroup>
                                    <Input
                                        id="sellerID"
                                        placeholder={this.state.sellerID}
                                        type="text"
                                        onChange={this.handleInputChange}
                                        ref={(input) => this.myinput = input} />
                            </FormGroup>
                        </Col>
                        <Col>
                            <Button
                                className="btn-round mr-1"
                                color="primary"
                                type="button"
                                onClick={this.handleSubmit}
                            >
                                Sell this out !!!
                </Button>
                        </Col>
                    </Row>
                        <br />
                    </form>
                </Container>
            </div>
        );
    }
}

export default Form;
