import React from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import Spinner from 'react-bootstrap/Spinner'

import {
    Button,
    Container,
    Row,
    Col,
    Card,
    CardBody,
    CardImg,
    CardTitle,
    CardText
} from "reactstrap";
//import { collapseTextChangeRangesAcrossMultipleVersions } from 'typescript';

class Item extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            posts: [],
            catalogue: [],
            item: {},
            currentIndex: 0,
            itemShow: 3,
            random: 0,
            checked: false,
            loading: true,
            error: null
        };
    }

    fetchBiddings = () => {
        console.log(`http://desmond.business:8080/fyp/getBiddingsByID/${this.props.id}`)
        axios.get(`http://desmond.business:8080/fyp/getBiddingByID/${this.props.id}`)
            .then(res => {
                // Transform the raw data by extracting the nested posts
                const updateCatalogue = this.state.catalogue
                const posts = res.data.results;
                console.log(posts)
                updateCatalogue.push(
                    <Col key={posts.id} className="col-md-4">
                        <Card style={{ width: '20rem' }}>
                            <CardImg top src={require("assets/img/mac_apple.jpg")} alt="..." />
                            <CardBody>
                                <CardTitle>{posts.title}</CardTitle>
                                <CardText>Create time: {posts.create_timestamp} Bidding Price: ${posts.selling_price} {posts.bidding_currency}</CardText>
                            </CardBody>
                        </Card>
                    </Col>
                )

                console.log(this.state.catalogue);
                //console.log(posts);
                // Update state to trigger a re-render.
                // Clear any errors, and turn off the loading indiciator.
                this.setState({
                    item: posts,
                    catalogue: updateCatalogue,
                    loading: false,
                    error: null
                });
            })
            .catch(err => {
                // Something went wrong. Save the error in state and re-render.
                this.setState({
                    loading: false,
                    error: err
                });
            });
    }

    handleSubmit = (event) => {
        event.preventDefault()
        axios.put('http://desmond.business:8080/fyp/postBidding', {
            "id": this.state.item.id,
            "title": this.state.item.title,
            "category_third_lv_id": this.state.item.selectedThirdCategory,
            "condition_id": this.state.condition_id,
            "details": this.state.details,
            "selling_price": this.state.item.sellingPrice,
            "bidding_price": this.state.bidding_price,
            "closing_date_timestmp": null,
            "theBiddingPhotos": {
                "id": 118,
                "photo1": null,
                "photo_type1": null,
                "photo2": null,
                "photo_type2": null,
                "photo3": null,
                "photo_type3": null,
                "create_timestamp": "2020-05-17 15:14:00",
                "modify_timestamp": null
            },
            "bidding_currency": this.state.bidding_currency,
            "district_id": this.state.district_id,
            "seller_customer_id": this.state.seller_customer_id,
            "buyer_customer_id": this.state.buyer_customer_id,
            "bidding_status_id": this.state.sellerID,
            "logistics_di_id": this.state.logistics_di_id,
            "payment_id": this.state.payment_id,
            "seller_ref_id": this.state.seller_ref_id,
            "buyer_ref_id": this.state.buyer_ref_id,
            "remarks": this.state.remarks,
            "create_timestamp": null,
            "modify_timestamp": null
        })
            .then(function (response) {
                console.log("Successful updating!!!")
                console.log(response);
            })
            .catch(function (error) {
                console.log("Error~~~ updating!!!")
                console.log(error);
            });
        //window.location.reload(false);
    }

    handleInputChange = (event) => {
        event.preventDefault()
        this.setState({
            [event.target.name]: event.target.value
        })
    }

    handlecheckBoxChange = (event) => {
        var checkBoxChange = this.state.checked
        this.setState({
            checked: !checkBoxChange
        })
    }

    componentDidMount() {
        // Remove the 'www.' to cause a CORS error (and see the error state)
        this.fetchBiddings()
    }

    showMore = () => {
        this.fetchBiddings()
        console.log(this.state.catalogue);
        console.log(this.state.currentIndex);
    }


    renderLoading() {
        return <div>Loading...</div>;
    }

    renderError() {
        return (
            <div>
                Uh oh: {this.state.error.message}
            </div>
        );
    }

    renderPosts() {
        if (this.state.error) {
            return this.renderError();
        }

        console.log(this.state.posts[0]);
        return (
            <h6>
                Success getting Bidding Records
            </h6>

        );
    }

    render() {
        const catalogue = this.state.catalogue
        if (this.state.loading) {
            return (
                <div className="section">
                    <Container className="text-center">
                        <Card className="text-center">
                            <Row>
                                <Col>
                                    <div className="title">
                                        <h1 className="bd-title">{this.props.title}</h1>
                                    </div>
                                </Col>
                            </Row>
                            <Spinner animation="border" variant="primary" />
                        </Card>
                    </Container>
                </div>
            )
        } else {
            return (
                <div className="section">
                    <Container className="text-center">
                        <Card className="text-center">
                            <Row>
                                <Col>
                                    <div className="title">
                                        <h1 className="bd-title">Item Page</h1>
                                    </div>
                                </Col>
                            </Row>
                            <CardBody>
                                <Row>
                                    <Col>
                                        {catalogue}
                                    </Col>
                                    <Col>
                                        <label>Post New Selling Price</label>
                                        <input
                                            id="sellingPrice"
                                            name="sellingPrice"
                                            type="text"
                                            class="form-control"
