import React from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import Spinner from 'react-bootstrap/Spinner'

import {
    Button,
    Container,
    Row,
    Col,
    Card,
    CardBody,
    CardImg,
    CardTitle,
    CardText
} from "reactstrap";
//import { collapseTextChangeRangesAcrossMultipleVersions } from 'typescript';

class Item extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            posts: [],
            catalogue: [],
            currentIndex: 0,
            itemShow: 3,
            random: 0,
            checked: false,
            loading: true,
            error: null
        };
    }

    fetchBiddings = () => {
        console.log(`http://desmond.business:8080/fyp/getBiddingsByID/${this.props.id}`)
        axios.get(`http://desmond.business:8080/fyp/getBiddingByID/${this.props.id}`)
            .then(res => {
                // Transform the raw data by extracting the nested posts
                const updateCatalogue = this.state.catalogue
                const posts = res.data.results;
                console.log(posts)
                updateCatalogue.push(
                    <Col key={posts.id} className="col-md-4">
                        <Card style={{ width: '20rem' }}>
                            <CardImg top src={require("assets/img/mac_apple.jpg")} alt="..." />
                            <CardBody>
                                <CardTitle>{posts.title}</CardTitle>
                                <CardText>Create time: {posts.create_timestamp} Bidding Price: ${posts.selling_price} {posts.bidding_currency}</CardText>
                            </CardBody>
                        </Card>
                    </Col>
                )

                console.log(this.state.catalogue);
                //console.log(posts);
                // Update state to trigger a re-render.
                // Clear any errors, and turn off the loading indiciator.
                this.setState({
                    posts,
                    catalogue: updateCatalogue,
                    loading: false,
                    error: null
                });
            })
            .catch(err => {
                // Something went wrong. Save the error in state and re-render.
                this.setState({
                    loading: false,
                    error: err
                });
            });
    }

    componentDidMount() {
        // Remove the 'www.' to cause a CORS error (and see the error state)
        this.fetchBiddings()
    }

    showMore = () => {
        this.fetchBiddings()
        console.log(this.state.catalogue);
        console.log(this.state.currentIndex);
    }


    renderLoading() {
        return <div>Loading...</div>;
    }

    renderError() {
        return (
            <div>
                Uh oh: {this.state.error.message}
            </div>
        );
    }

    renderPosts() {
        if (this.state.error) {
            return this.renderError();
        }

        console.log(this.state.posts[0]);
        return (
            <h6>
                Success getting Bidding Records
            </h6>

        );
    }

    render() {
        const catalogue = this.state.catalogue
        if (this.state.loading) {
            return (
                <div className="section">
                    <Container className="text-center">
                        <Card className="text-center">
                            <Row>
                                <Col>
                                    <div className="title">
                                        <h1 className="bd-title">{this.props.title}</h1>
                                    </div>
                                </Col>
                            </Row>
                            <Spinner animation="border" variant="primary" />
                        </Card>
                    </Container>
                </div>
            )
        } else {
            return (
                <div className="section">
                    <Container className="text-center">
                        <Card className="text-center">
                            <Row>
                                <Col>
                                    <div className="title">
                                        <h1 className="bd-title">Item Page</h1>
                                    </div>
                                </Col>
                            </Row>
                            <CardBody>
                                <Row>
                                    <Col>
                                        {catalogue}
                                    </Col>
                                    <Col>
                                        <label>Post New Selling Price</label>
                                        <input
                                            id="sellingPrice"
                                            name="sellingPrice"
                                            type="text"
                                            class="form-control"
                                            placeholder="Selling Price..."
                                        />

                                        <form onSubmit={this.handleSubmit}>

                                            <div class="form-group">
                                                <label>Title</label>
                                                <input
                                                    type="text"
                                                    class="form-control"
                                                    id="title"
                                                    name="title"
                                                    placeholder="Title..."
                                                    onChange={this.handleInputChange}
                                                    ref={(input) => this.myinput = input}
                                                />
                                            </div>
                                            <div class="form-group">
                                                <label>Main Category</label>
                                                <select
                                                    id="selectedMainCategory"
                                                    name="selectedMainCategory"
                                                    class="form-control"
                                                    onChange={this.handleMainCategoryChange}
                                                    ref={(input) => this.myinput = input}
                                                >
                                                    {this.state.mainCategory}
                                                </select>
                                            </div>
                                            <div class="form-group">
                                                <label>Sub Category</label>
                                                <select
                                                    id="selectedSubCategory"
                                                    name="selectedSubCategory"
                                                    class="form-control"
                                                    onChange={this.handleSubCategoryChange}
                                                    ref={(input) => this.myinput = input}
                                                >
                                                    <option selected>Choose Main Category...</option>
                                                    {this.state.subCategory}
                                                </select>
                                            </div>
                                            <div class="form-group">
                                                <label>Third Category</label>
                                                <select
                                                    id="selectedThirdCategory"
                                                    name="selectedThirdCategory"
                                                    class="form-control"
                                                    onChange={this.handleInputChange}
                                                    ref={(input) => this.myinput = input}
                                                >
                                                    <option selected>Choose Sub Category...</option>
                                                    {this.state.thirdCategory}
                                                </select>
                                            </div>
                                            <div class="form-group">
                                                <label>Remark</label>
                                                <input
                                                    id="remark"
                                                    name="remark"
                                                    type="text"
                                                    class="form-control"
                                                    placeholder="Remark..."
                                                    onChange={this.handleInputChange}
                                                    ref={(input) => this.myinput = input}
                                                />
                                            </div>

                                            <div class="form-row">
                                                <div class="form-group col-md-4">
                                                    <label>Selling Price</label>
                                                    <input
                                                        id="sellingPrice"
                                                        name="sellingPrice"
                                                        type="text"
                                                        class="form-control"
                                                        placeholder="Selling Price..."
                                                    />
                                                </div>
                                                <div class="form-group col-md-2">
                                                    <label>Currency</label>
                                                    <select id="currency"
