import React from 'react'

class CarsProperty extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            students: [
                { car: 'Cars for Sale', property: 'Industrial property' },
                { car: 'Vehicle Rentals', property: 'Rentals' },
            ]
        }
    }

    renderCarsProperty = () => {
        const renderMainCategory = []
        renderMainCategory.push(
            <option selected>Choose Main Category...</option>
        )
        axios.get("http://desmond.business:8080/fyp/getCategoryFirstLvs")
            .then(res => {
                // Transform the raw data by extracting the nested posts

                const posts = res.data.results;
                //console.log(posts);
                // Update state to trigger a re-render.
                // Clear any errors, and turn off the loading indiciator.
                posts.forEach((elm) => {

                    renderMainCategory.push(
                        <option
                            value={elm.id}
                        >{elm.category_first_lv_name}
                        </option>
                    );
                })
                this.setState({
                    category: posts,
                    mainCategory: renderMainCategory,
                    loading: false,
                    error: null
                });
            })
            .catch(err => {
                // Something went wrong. Save the error in state and re-render.
                this.setState({
                    loading: false,
                    error: err
                });
            });

    }

    renderTableHeader() {
        let header = Object.keys(this.state.students[0])
        return header.map((key, index) => {
            return <th key={index}>{key.toUpperCase()}</th>
        })
    }

    renderTableData() {
        return this.state.students.map((student, index) => {
            const { car, property } = student //destructuring
            return (
                <tr key={car}>
                    <td><a href="/CarsForSale">{car}</a></td>
                    <td>{property}</td>
                </tr>
            )
        })
    }

    render() {
        return (
            <div>
                <table id='students'>
                    <tbody>
                        <tr>{this.renderTableHeader()}</tr>
                        {this.renderTableData()}
                    </tbody>
                </table>
            </div>
        )
    }
}

export default CarsProperty;
