import React, { Component } from 'react'

class Bidding extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            list: [],
            error: null
        }

        
    }

    buildList = (data) => {
        console.log(data);
        this.setState({ list: data });
     }

    componentDidMount() {
        let url = "http://desmond.business:8080/fyp/getBiddings"
        fetch(url)
            .then(response => response.json())
            .then(this.buildList)
            .catch(error => {
                this.setState
            })
        
    }

    render() {
        return (
            <div>
                <h1>Home</h1>
                <ul>
                    {
                        this.state.length === 0 && <li>Sorry No data exist</li>
                    }
                    {
                        this.state.list > 0 && this.state.list.map((item) => (<li>{item.title + " " + item.id}</li>))
                    }
                </ul>
                {!this.state.error && <h3>{this.state.error}</h3>}
            </div>
        )
    }
}

export default Bidding;
