import React from 'react'
import axios from 'axios';

class CarsProperty extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            loading: false,
            error: null,
            carsPropertyHeader:[],
            carsPropertyItem: [],
            category:"Cars & Property"
        }
    }

    componentDidMount(){
        this.renderCarsPropertyHeader()
        this.renderCarsPropertyItem()
    }

    renderCarsPropertyHeader = () => {

        return(
        axios.get("http://desmond.business:8080/fyp/getCategoryFirstLvs")
            .then(res => {
                // Transform the raw data by extracting the nested posts

                const posts = res.data.results;
                //console.log(posts);
                // Update state to trigger a re-render.
                // Clear any errors, and turn off the loading indiciator.
                posts.forEach((elm) => {
                    if(elm.category_first_lv_name == this.state.category){
                        console.log(elm)
                    this.state.carsPropertyItem.push(elm.theCategorySecondLvs)
                    }
                })
                console.log("==========================")
                console.log(this.state.carsPropertyItem)
                this.setState({
                    loading: false,
                    error: null
                });
            })
            .catch(err => {
                // Something went wrong. Save the error in state and re-render.
                this.setState({
                    loading: false,
                    error: err
                });
            })
        )
    }

    renderCarsPropertyItem = () => {

        return(
        axios.get("http://desmond.business:8080/fyp/getCategoryFirstLvs")
            .then(res => {
                // Transform the raw data by extracting the nested posts

                const posts = res.data.results;
                //console.log(posts);
                // Update state to trigger a re-render.
                // Clear any errors, and turn off the loading indiciator.
                posts.forEach((elm) => {
                    if(elm.category_first_lv_name == this.state.category){
                    elm.theCategorySecondLvs.forEach(subElm=>{
                        console.log("Found subElm")
                        console.log(subElm)
                        const carsPropertyItemList = []
                        carsPropertyItemList.push(subElm.theCategoryThirdLvLv.map((obj,index)=>
                        <td id={obj.id}><a href="/CarsForSale">{obj.category_third_lv_name}</a></td>
                        )
                        )

                        this.state.carsPropertyItem.push(
                            <tr>
                                {carsPropertyItemList}
                            </tr>
                        )
                    })
                    }
                })

                console.log(this.state.carsProperty)
                this.setState({
                    loading: false,
                    error: null
                });
            })
            .catch(err => {
                // Something went wrong. Save the error in state and re-render.
                this.setState({
                    loading: false,
                    error: err
                });
            })
        )
    }

    render() {
        return (
            <div>
                <table id='carsPropertyTable'>
                    <tbody>
                        <tr>
                            {this.state.carsPropertyHeader}
                            </tr>
                        <tr key="1">
                        {this.state.carsPropertyItem}
                            </tr>
                    </tbody>
                </table>
            </div>
        )
    }
}

export default CarsProperty;
