import React from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';

import {
    Button,
    FormGroup,
    Input,
    Container,
    Row,
    Col
} from "reactstrap";

class Form extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            posts: [],
            loading: true,
            error: null
        };
    }

    postForm(lastID,title,price,sellerID) {
        axios.post("http://desmond.business:8080/fyp/postBidding", {
            "id": lastID,
            "title": title,
            "category_third_lv_id": null,
            "condition_id": null,
            "details": null,
            "selling_price": null,
            "bidding_price": price,
            "closing_date_timestmp": null,
            "theBiddingPhotos": {
                "id": 118,
                "photo1": null,
                "photo_type1": null,
                "photo2": null,
                "photo_type2": null,
                "photo3": null,
                "photo_type3": null,
                "create_timestamp": "2020-05-17 15:14:00",
                "modify_timestamp": null
            },
            "bidding_currency": null,
            "district_id": null,
            "seller_customer_id": sellerID,
            "buyer_customer_id": null,
            "bidding_status_id": 1,
            "logistics_di_id": null,
            "payment_id": null,
            "seller_ref_id": null,
            "buyer_ref_id": null,
            "remarks": null,
            "create_timestamp": "2020-05-17 15:14:00",
            "modify_timestamp": null
        })
            .then(function (response) {
                console.log(response);
            })
            .catch(function (error) {
                console.log(error);
            });
    }

    componentDidMount() {
        // Remove the 'www.' to cause a CORS error (and see the error state)
        axios.get("http://desmond.business:8080/fyp/getBiddings")
            .then(res => {
                // Transform the raw data by extracting the nested posts

                const posts = res.data.results.map(obj => obj.id);
                //console.log(posts);
                // Update state to trigger a re-render.
                // Clear any errors, and turn off the loading indiciator.
                this.setState({
                    posts,
                    loading: false,
                    error: null
                });
            })
            .catch(err => {
                // Something went wrong. Save the error in state and re-render.
                this.setState({
                    loading: false,
                    error: err
                });
            });
    }

    renderLoading() {
        return <div>Loading...</div>;
    }

    renderError() {
        return (
            <div>
                Uh oh: {this.state.error.message}
            </div>
        );
    }

    renderPosts() {
        if (this.state.error) {
            return this.renderError();
        }

        console.log(this.state.posts[0]);
        return (
            <h6>
                Success getting Bidding Records
            </h6>
        );
    }

    render() {
        const { posts } = this.state;
        return (
            
            <h6>null</h6>
        );
    }
}

export default Form;
