import React from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';
import Modal from 'react-modal';
import CustomerModal from 'components/Modal.js';

import {
    Button,
    FormGroup,
    Input,
    Container,
    Row,
    Col,
    Card,
    CardBody,
    CardImg,
    CardTitle,
    CardText
} from "reactstrap";
import { collapseTextChangeRangesAcrossMultipleVersions, createSemanticDiagnosticsBuilderProgram } from 'typescript';

class Cars extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            posts: [],
            catalogue: null,
            currentIndex : 0,
            loading: true,
            error: null
        };
    }
    
    updateCatalogue() {
        const catalogue = Array()
        axios.get("http://desmond.business:8080/fyp/getBiddings")
            .then(res => {
                // Transform the raw data by extracting the nested posts
                const posts = res.data.results;
                //console.log(posts);
                // Update state to trigger a re-render.
                // Clear any errors, and turn off the loading indiciator.
                
                this.setState({
                    posts: posts,
                    catalogue:null,
                    loading: false,
                    error: null
                });
            })
            .catch(err => {
                // Something went wrong. Save the error in state and re-render.
                this.setState({
                    loading: false,
                    error: err
                });
            });
    }

    renderCatalogue() {
        const posts = this.state.posts;
        const catalogue = this.state.catalogue;
        const currentIndex = this.state.currentIndex;
        console.log(posts);
        /*
        for (let index = currentIndex; index < currentIndex + 10 ; index++) {
            catalogue.push(
                <Card style={{ width: '20rem' }}>
                    <CardImg top src={require("assets/img/mac_apple.jpg")} alt="..." />
                    <CardBody>
                        <CardTitle>{this.state.posts[index].title}</CardTitle>
                        <CardText>Give you best performance</CardText>
                        <Button color="primary">$5400000</Button>
                    </CardBody>
                </Card>
            )
        }
                    */
    }

    componentDidMount() {
        // Remove the 'www.' to cause a CORS error (and see the error state)
        this.updateCatalogue()
        this.renderCatalogue()
    }

    renderLoading() {
        return <div>Loading...</div>;
    }

    renderError() {
        return (
            <div>
                Uh oh: {this.state.error.message}
            </div>
        );
    }

    renderPosts() {
        if (this.state.error) {
            return this.renderError();
        }

        console.log(this.state.posts);
        return (
            <h6>
                Success getting Bidding Records
            </h6>
            
        );
    }

    render() {
        const catalogue = this.state.catalogue

        return (

            <div className="section">

                <Container className="text-center">
                    <Card className="text-center">
                        <CardBody>
                            <Row>
                                {catalogue}
                            </Row>
                            <Row>
                                <Button
                                    className="btn-round mr-1"
                                    color="primary"
                                    type="button"
                                    onClick={this.showMore}
                                >
                                    Show More
                            </Button>
                            </Row>
                        </CardBody>
                    </Card>
                </Container>
            </div>
        );
    }
}

export default Cars;
